mapper.select:
select *, if(
  exists(
    select user_id from subscribe_list
      where message_type.id  = subscribe_list.type_id
        and subscribe_list.user_id = #{id}
  ), 0, 1) as operation
from message_type
where 1 =1

    and message_type.name like concat(concat('%',#{name}),'%');

select count(1) from message_type
where 1 = 1

    and message_type.name like concat(concat('%',#{name}),'%');
