mapper.select:
select user.id, user.name, user.role from user inner join subscribe_list on user.id = subscribe_list.user_id where 1 =1
and user.name like concat(concat('%',#{name}),'%')
and subscribe_list.type_id = #{typeId}

select * from user where 1 = 1

 AND role = #{role}


    AND name = like concat(concat('%',#{name}),'%')


    AND whetherApplying = #{whetherApplying}

select count(1) from user where 1 = 1

    AND role = #{role}


    AND name like concat(concat('%',#{name}),'%')


    AND whetherApplying = #{whetherApplying}

mapper.update:
update user set role = #{role}

    , whetherApplying = #{whetherApplying}

where id = #{id}
