mapper.select:
select * from user where 1 = 1

 AND role = #{role}


    AND name = #{name}


    AND flag = #{flag}

select count(1) from user where 1 = 1

    AND role = #{role}


    AND name like concat(concat('%',#{name}),'%')


    AND flag = #{flag}

mapper.update:
update user set role = #{role}

    , flag = #{flag}

where id = #{id}
